package tema2;

public class TutorialesYaFor03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Definimos la variable
		int i;

		// El valor inicial de i sera 5
		// El bucle se ejecutará mientras el valor de i sea menor o igual a 50
		// Al final del bucle el valor de i incrementará en 5
		for (i = 5; i <= 50; i += 5) {
			// Informara por pantalla del valor de i
			System.out.println(i);
		}

	}

}
