package tema2;

public class TutorialesYaWhile04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Inicializamos las variables
		int i = 0;
		int n = 0;

		// Mientra i sea menor que 25 se ejecutaran las instrucciones
		while (i < 25) {
			// Al valor de n se le sumará 11
			n += 11;

			// Imprimimos en pantalla el numero
			System.out.println(n);

			// Incrementamos el valor de i
			i++;
		}

	}

}
