package tema2;

public class TutorialesYaWhile05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Inicializamos las variables
		int n = 0;

		// Mientra n sea menor que 800 se ejecutaran las instrucciones
		while (n < 800) {
			// Al valor de n se le sumará 8
			n += 8;

			// Imprimimos en pantalla el numero
			System.out.println(n);

		}

	}

}
